def print_title_screen(title_screen):

  global line_width
  global line_height

  print("-"*line_width)
  if line_width > 80:
    h_offset = round((line_width - 80)/2)
  else:
    h_offset = 0
  print('\33[31m' + '█'*line_width + '\33[0m')
  print("-"*line_width)
  if line_height > 25:
    v_offset = round((line_height - 34) / 2)
  else:
    v_offset = 0
  print("\n"*v_offset)
  for line in title_screen:
    print(" "*h_offset + line)
    time.sleep(0.1)
  print("\n"*v_offset)
  print("-"*line_width)
  print('\33[31m' + '█'*line_width + '\33[0m')
  print("-"*line_width)
  input(" "*round((line_width - 27)/2) + "Press enter to continue...")
  os.system('cls' if os.name == 'nt' else 'clear')

