# functions for printing the lore text pretty, easily.
def print_lore(s):
    global sleep_speed

    l = s.split("/LINE ")

    for s in l:
        for c in s:
            sys.stdout.write('\033[0;33m' + c + '\33[0m')
            sys.stdout.flush()
            time.sleep(sleep_speed/10)
        print(" ")

def decrypt(b):
    b = [b[i:i+8] for i in range(0, len(b), 8)]
    return ''.join([chr(int(x, 2)) for x in b])

